// src/components/LoginForm.jsx
import React, { useState } from "react";

export default function LoginForm({ onLogin }) {
  const [role, setRole] = useState("freelancer");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    // Mock login — replace with API call
    onLogin({ email, role });
  }

  return (
    <div className="max-w-md w-full bg-white p-6 rounded shadow-md">
      <h2 className="text-2xl font-semibold mb-4">Login / Register</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block font-medium mb-1">Email</label>
          <input
            type="email"
            className="w-full border border-gray-300 rounded p-2 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div>
          <label className="block font-medium mb-1">Password</label>
          <input
            type="password"
            className="w-full border border-gray-300 rounded p-2 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div>
          <span className="block font-medium mb-1">Role</span>
          <label className="inline-flex items-center mr-6">
            <input
              type="radio"
              value="freelancer"
              checked={role === "freelancer"}
              onChange={() => setRole("freelancer")}
              className="form-radio text-indigo-600"
            />
            <span className="ml-2">Freelancer</span>
          </label>
          <label className="inline-flex items-center">
            <input
              type="radio"
              value="client"
              checked={role === "client"}
              onChange={() => setRole("client")}
              className="form-radio text-indigo-600"
            />
            <span className="ml-2">Client</span>
          </label>
        </div>

        <button
          type="submit"
          className="w-full bg-indigo-600 text-white font-semibold py-2 rounded hover:bg-indigo-700"
        >
          Login / Register
        </button>
      </form>
    </div>
  );
}
